INSERT INTO `events` (`id`, `state`, `slug`, `title`, `date`, `time`, `place`, `descripcion`, `price`, `user_id`, `created_at`, `updated_at`) VALUES
(3, '0', 'star-party-valle-grande-2020-observacion-publica', 'Star Party Valle Grande 2020 - Observación Pública', '2020-04-17', '22:00', 'Villa Santa Clara del Atuel, ruta provincial 173 km 12,5, Valle Grande, San Rafael, Mendoza', '<p>Dentro del marco de la tradicional Star Party Valle Grande realizamos una charla con observación para el público en general.&nbsp;</p><p>La charla se titula <em>Un recorrido por el cielo con telescopio</em><span style=\"text-align: justify;\">&nbsp;con imágenes en tiempo real a cargo de Jaime García y César Brollo.</span><br></p><p><span style=\"text-align: justify;\">Para participar es necesario inscribirse previamente contactándose con la organización del evento a través del WhatsApp de Cecilia Luraschi&nbsp; &nbsp; &nbsp;</span></p><p align=\"right\"><a href=\"https://api.whatsapp.com/send?phone=5492604605794\" target=\"_blank\">\r\n<img src=\"http://www.ppi.com.ar/img/btn_whatsapp.png\" alt=\"\">\r\n</a></p>\r\n<p><span style=\"text-align: justify;\"><br></span></p>', '$200', 3, '2020-01-11 05:27:37', '2020-01-11 05:27:37');
